function setDefaultViewMap(frm) {
  // set view map
  let map = frm.get_field("map").map;
  let longitude = frm.doc.longitude || 109.358;
  let latitude = frm.doc.latitude || 15.961;
  map.setView([latitude, longitude], 5);
}

function setFieldValue(frm, latitude = 0, longitude = 0, address) {
  frm.set_value("location", JSON.stringify({longitude,latitude}));
  frm.set_value("address", address);
}

frappe.ui.form.on("Customer", {
  refresh: function (frm) {

  },
    onload:async function(frm) {

     let customer_primary_address = frm.doc?.customer_primary_address
     if(customer_primary_address) {
      let adr_location = frm.doc.__onload.addr_list.find((address)=> {
        return address.name == customer_primary_address
     })?.address_location
         frm.set_value("customer_location_primary",adr_location)

     }

    }
});

frappe.ui.form.on("Customer", {
  refresh: function (frm) {

  },
customer_primary_address:async function(frm) {
    let primaryAddressObj = frm.doc.__onload.addr_list.find(address => {
        return address.name == frm.doc.customer_primary_address
    })
    if(primaryAddressObj) {
        frm.set_value('customer_location_primary',primaryAddressObj.address_location)
    }

},

});

function setLocation(frm, lat = 0, long = 0, address) {
  frm.set_value("address_location", JSON.stringify({long,lat}));
}
async function setTitle(frm) {
    let country = frm.doc.country
    let city = frm.doc.city || ""
    let county = frm.doc.county || ""
    let state = frm.doc.state  || ""
    let address_line1 = frm.doc.address_line1 || ""
    //get name city
    if(city){
	    let cityRs=await frm.call({
	        type: "GET",
		  method: "mbw_dms.api.helpers.address.get_name_city",
		  args: {
			name: frm.doc.city
		  }
		})
		city = cityRs.result
    }


    //get name district
    if(county){
		let countyRs= await frm.call({
	        type: "GET",
		  method: "mbw_dms.api.helpers.address.get_name_district",
		  args: {
			name: frm.doc.county
		  }
		})
		county = countyRs.result
    }
    //get name ward
    if(state) {
       let stateRs = await frm.call({
	        type: "GET",
		  method: "mbw_dms.api.helpers.address.get_name_ward",
		  args: {
			name: frm.doc.state
		  }
		})
		state = stateRs.result
    }



    frm.set_value("address_title", `${address_line1}, ${state}, ${county}, ${city}, ${country}`);
}
frappe.ui.form.on('Address', {
    address_title: async (frm) => {
        let country = frm.doc.country
        let city = frm.doc.city || ""
        let county = frm.doc.county || ""
        let state = frm.doc.state  || ""
        let address_line1 = frm.doc.address_line1 || ""
        if (country && city&& county && state && address_line1) {
            let address = frm.doc.address_title
            await frappe.call({
				type: "GET",
				method: "mbw_service_v2.api.ess.geolocation.get_coordinates_location",
				args: {
					"address": address
				},
				callback: function (r) {
					let rs = r.result.results;
					if(rs) {
						let {geometry: {location}} = rs[0]
						console.log("location",location);
						frm.set_value('custom_address_on_map',JSON.stringify(
							[{"type":"Feature","properties":{},"geometry":{"type":"Point","coordinates":[location.lng,location.lat]}}]
							))
						frm.set_value('address_location',JSON.stringify({long: location.lng,lat:location.lat}))
					}
				  },
			})
			console.log(frm.doc)
        }
    },
    onload: function(frm) {


	},
	refresh(frm) {
		// your code here
	},
	county: async (frm)=> {
	   await setTitle(frm)
	    frm.set_query('state',() =>{ 
		return {
			filters: {
				ma_quan_huyen:frm.doc.county
			}
		}
		})

	},
	address_line1: (frm)=> {
	    setTitle(frm)
	},
	city: async (frm)=> {
	    await setTitle(frm)

	    frm.set_query('county',() =>{ 
			return {
				filters: {
					ma_tinh_thanh: Number.parseInt( frm.doc.city)
				}
			}
		})

	},
	country: (frm)=> {
	    setTitle(frm)
	},
	state: async (frm)=> {
	    await setTitle(frm)
	    let name_district

	}
})
