async function getInfo(doc) {
    let response = await frappe.call({
        method: "mbw_dms.api.vgm.get_info",
        args: {
            name: doc.name
        }
    })
    if(!response) {
        frappe.msgprint(__("Please select items to perform the action."));
    }
    doc = response.message
    let address;
    if(doc.customer_primary_address) {
        address = doc.customer_primary_address.split("-")[0]
    }
    let location
    if(doc.customer_location_primary) {
        let jsonLocation = JSON.parse(doc.customer_location_primary);
        location =[jsonLocation.long,jsonLocation.lat]
    }
    return {
                "name":doc.name,
                "address": address || "",
                "coordinate": location || []
              }
}

