function convertNumberToWords(number) {
    const units = ["", "một", "hai", "ba", "bốn", "năm", "sáu", "bảy", "tám", "chín"];
    const teens = ["mười", "mười một", "mười hai", "mười ba", "mười bốn", "mười lăm", "mười sáu", "mười bảy", "mười tám", "mười chín"];
    const tens = ["", "", "hai mươi", "ba mươi", "bốn mươi", "năm mươi", "sáu mươi", "bảy mươi", "tám mươi", "chín mươi"];
    const thousands = ["", "ngàn", "triệu", "tỷ"];

    if (number === 0) return "không đồng chẵn";

    number = number.toString();
    number = number.replace(/[^0-9]*/g, '');
    let words = [];
    let j = 0;

    while (number.length > 0) {
        let chunk = parseInt(number.substr(-3));
        number = number.substr(0, number.length - 3);

        if (chunk) {
            let chunkWords = [];
            if (chunk > 99) {
                chunkWords.push(units[Math.floor(chunk / 100)]);
                chunkWords.push("trăm");
                chunk = chunk % 100;
            }

            if (chunk > 19) {
                chunkWords.push(tens[Math.floor(chunk / 10)]);
                chunk = chunk % 10;
                if (chunk) chunkWords.push(units[chunk]);
            } else if (chunk > 0) {
                chunkWords.push(teens[chunk - 10]);
            }

            if (chunkWords.length > 0) {
                chunkWords.push(thousands[j]);
            }

            words.unshift(chunkWords.join(" "));
        }

        j++;
    }

    return words.join(" ") + " đồng chẵn";
}

frappe.ui.form.on('Payment Entry', {
    refresh: function(frm) {
        if (frm.doc.party_type === 'Employee') {
            frm.trigger('calculate_paid_amount');
        }
    },

    validate: function(frm) {
        if (frm.doc.party_type === 'Employee') {
            frm.trigger('calculate_paid_amount');
        }
    },

    customer_paid: function(frm) {
        if (frm.doc.party_type === 'Employee') {
            frm.trigger('calculate_paid_amount');
        }
    },

    custom_debt_note_information: function(frm) {
        if (frm.doc.party_type === 'Employee') {
            frm.trigger('calculate_paid_amount');
        }
    },

    calculate_paid_amount: function(frm) {
        let total_debt_amount = 0;
        let customer_paid = frm.doc.customer_paid || 0;

        if (frm.doc.custom_debt_note_information) {
            frm.doc.custom_debt_note_information.forEach(function(debt_note) {
                total_debt_amount += debt_note.debt_amount || 0;
            });
        }

        frm.set_value('paid_amount', total_debt_amount + customer_paid);
    }
});

