function calculate_totals(frm) {
    let total_amount_before_discount = 0;
    let total_discount_on_product = 0;

    // Loop through all rows in the child table custom_request_for_delivery_noteee
    $.each(frm.doc.custom_request_for_delivery_note || [], function(i, row) {
        total_amount_before_discount += row.custom_amount_before_discount || 0;
        total_discount_on_product += row.custom_discount_on_total || 0;
    });

    // Set the value for custom_amount_before_discount and custom_discount_on_product in the parent doctype
    frm.set_value('custom_amount_before_discount', total_amount_before_discount);
    frm.set_value('custom_discount_on_product', total_discount_on_product);
}

