function calculate_round_total(frm) {
    let amount_before_discount = frm.doc.custom_amount_before_discount || 0;
    let discount_on_product = frm.doc.custom_discount_on_product || 0;
    let total_discount_on_order = frm.doc.custom_total_discount_on_order || 0;

    let round_total = amount_before_discount - discount_on_product - total_discount_on_order;

    // Set the calculated value in the custom_round_total field
    frm.set_value('custom_round_total', round_total);
}

