// Hàm để định dạng ngày tháng theo kiểu Việt Nam
function format_date_to_vietnamese(date) {
    let parts = date.split('-'); // yyyy-mm-dd
    let day = parts[2];
    let month = parts[1];
    let year = parts[0];
    return `Ngày ${day} tháng ${month} năm ${year}`;
}

