script:
frappe.ui.form.on("Customer", {
  refresh: function (frm) {

  },
onload:async function(frm) {
    console.log('list_address',frm.doc.__onload.addr_list)
    let primaryAddressObj = frm.doc.__onload.addr_list.find(address => address.is_primary_address == 1)
    if(primaryAddressObj) {
        frm.set_value('customer_location_primary',primaryAddressObj.address_location)
        frm.doc.save()
    }

},

});
